# Logistic bifurcation: small r-changes, big behavior shifts
def step(r, x=0.5, n=50):
 for _ in range(n): x = r*x*(1-x)
 return x
for r in [2.5, 3.0, 3.5, 3.9]:
 print(f"r={r}: x‚âà{round(step(r),3)}")
